package bd.edu.seu.messmanagementsystemfrontend.model;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;@Data@AllArgsConstructor@NoArgsConstructorpublic class Bazar {    private long bazarNo;    private String name;    private double unit;    private double costPerUnit;    @Override    public String toString() {        return "Name: "+name+"    Unit: "+unit+"    UnitPrice: "+costPerUnit+",\n";    }}