package bd.edu.seu.messmanagementsystemfrontend.service;import bd.edu.seu.messmanagementsystemfrontend.exceptions.ResourceNotFoundException;import bd.edu.seu.messmanagementsystemfrontend.model.DailyActivities;import bd.edu.seu.messmanagementsystemfrontend.model.Evaluation;import org.springframework.beans.factory.annotation.Value;import org.springframework.core.ParameterizedTypeReference;import org.springframework.http.HttpMethod;import org.springframework.http.ResponseEntity;import org.springframework.stereotype.Service;import org.springframework.web.client.RestTemplate;import java.io.DataOutput;import java.time.LocalDate;import java.util.ArrayList;import java.util.List;@Servicepublic class AdminService {    @Value("${BASE_URL}")    String baseUrl;    private RestTemplate restTemplate;    public AdminService() {        restTemplate = new RestTemplate();    }    public List<Evaluation> getManagerEvaluation(String managerNameValue, LocalDate date) {        try {            int month = date.getMonthValue();            int year = date.getYear();            ResponseEntity<List<Evaluation>> responseEntity = restTemplate.exchange(                    baseUrl + "/evaluation/" + month + "/" + year + "/" + managerNameValue,                    HttpMethod.GET,                    null,                    new ParameterizedTypeReference<List<Evaluation>>() {                    });            List<Evaluation> responseEntityBody = responseEntity.getBody();            return responseEntityBody;        }catch (Exception e){            return new ArrayList<>();        }    }    public double getAvgManagerEvaluation(String managerNameValue, LocalDate date) {        try {            int month = date.getMonthValue();            int year = date.getYear();            ResponseEntity<Double> responseEntity = restTemplate.exchange(                    baseUrl + "/evaluation/avg/" + month + "/" + year + "/" + managerNameValue,                    HttpMethod.GET,                    null,                    new ParameterizedTypeReference<Double>() {                    });            double responseEntityBody = responseEntity.getBody();            return responseEntityBody;        }catch (Exception e){            return 0;        }    }}