package bd.edu.seu.messmanagementsystemfrontend.service;import bd.edu.seu.messmanagementsystemfrontend.model.*;import org.springframework.beans.factory.annotation.Value;import org.springframework.http.HttpEntity;import org.springframework.stereotype.Service;import org.springframework.web.client.RestTemplate;import java.time.LocalDate;@Servicepublic class RegistrationService {    private RestTemplate restTemplate;    @Value("${BASE_URL}")    private String baseURL;    public RegistrationService() {        restTemplate =new RestTemplate();    }    public String register(MemberManager user) {       try {               HttpEntity<MemberManager> request = new HttpEntity<>(user);               restTemplate.postForObject(baseURL+"/member_manager", request, MemberManager.class);               return "Success";       }catch (Exception e){           return "Error "+e.getMessage();       }    }    public String saveLoginInformation(LoginInformation loginInformation) {        try {            loginInformation.setRole(Role.Member);            HttpEntity<LoginInformation> requestForLogin = new HttpEntity<>(loginInformation);            restTemplate.postForObject(baseURL + "/login", requestForLogin, LoginInformation.class);            return "Success";        }catch (Exception e){            return "Error....";        }    }}