package bd.edu.seu.messmanagementsystemfrontend.ui.admin;import com.vaadin.flow.component.html.Image;import com.vaadin.flow.component.menubar.MenuBar;import com.vaadin.flow.component.orderedlayout.HorizontalLayout;import javax.servlet.http.HttpSession;import java.util.stream.Stream;public class AdminHeadeer extends HorizontalLayout {    public AdminHeadeer(HttpSession httpSession) {        super();        Image image = new Image();        image.setWidth("120px");        image.setHeight("40px");        image.setSrc("https://logos.textgiraffe.com/logos/logo-name/Admin-designstyle-boots-m.png");        MenuBar menuBar = new MenuBar();        Stream.of("Dashboard","MemberAndManagerList","Manager View","Evaluation","SignOut")                .forEach(menuBar::addItem);        add(image,menuBar);        menuBar.setSizeFull();        menuBar.getItems().forEach(menuItem -> {            menuItem.addClickListener(menuItemClickEvent -> {                if(menuItem.getText().equals("Dashboard")){                    httpSession.setAttribute("user",httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("admin"));                }else if(menuItem.getText().equals("Manager View")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("manager"));                }else if(menuItem.getText().equals("Evaluation")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("show_evaluation"));                }else if(menuItem.getText().equals("MemberAndManagerList")){                    httpSession.setAttribute("user",httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("memberAndManager"));                }else{                    httpSession.removeAttribute("user");                    menuItem.getUI().ifPresent(ui -> ui.navigate("login"));                }            });        });    }}