package bd.edu.seu.messmanagementsystemfrontend.ui.admin;import bd.edu.seu.messmanagementsystemfrontend.ui.Footer;import com.vaadin.flow.component.html.Div;import com.vaadin.flow.component.html.Image;import com.vaadin.flow.component.notification.Notification;import com.vaadin.flow.component.orderedlayout.VerticalLayout;import com.vaadin.flow.router.Route;import javax.servlet.http.HttpSession;@Route("admin")public class AdminView extends VerticalLayout {    public AdminView(HttpSession httpSession) {        super();        AdminHeadeer adminHeadeer = new AdminHeadeer(httpSession);        adminHeadeer.addAttachListener(attachEvent -> {            String userRole =(String) httpSession.getAttribute("user");            if(userRole==null) userRole="unknown";            Notification.show(userRole);            if(!userRole.equals("Admin")){                httpSession.removeAttribute("user");                adminHeadeer.getUI().ifPresent(ui -> ui.navigate("login"));            }        });        Div body = new Div();        Image image = new Image();        image.setSrc("https://thumbs.dreamstime.com/b/palabra-admin-de-la-forma-del-alfabeto-del-plasticine-31820412.jpg");        image.setSizeFull();        image.setWidthFull();        body.add(image);        body.setHeightFull();        Footer footer = new Footer();        add(adminHeadeer,body,footer);    }}