package bd.edu.seu.messmanagementsystemfrontend.ui.manager;import com.vaadin.flow.component.html.Image;import com.vaadin.flow.component.menubar.MenuBar;import com.vaadin.flow.component.orderedlayout.HorizontalLayout;import javax.servlet.http.HttpSession;import java.util.stream.Stream;public class ManagerHeader extends HorizontalLayout {    public ManagerHeader(HttpSession httpSession) {        super();        Image image = new Image();        image.setWidth("120px");        image.setHeight("40px");        image.setSrc("https://st2.depositphotos.com/3591429/6308/i/950/depositphotos_63082979-stock-photo-hands-holding-word-manager.jpg");        MenuBar menuBar = new MenuBar();        if(httpSession.getAttribute("user")!=null && httpSession.getAttribute("user").equals("Admin")){            menuBar.addItem("Back to Admin View");        }        Stream.of("DeshBoard", "Activities", "Meal", "Accounts", "MemberList","Member-View", "SignOut").forEach(menuBar::addItem);        menuBar.addThemeVariants();        menuBar.getItems().forEach(menuItem -> {            menuItem.addClickListener(menuItemClickEvent -> {                if(menuItem.getText().equals("DeshBoard")){                    httpSession.setAttribute("user",httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("manager"));                }else if(menuItem.getText().equals("Activities")){                    httpSession.setAttribute("user",httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("activities"));                }else if(menuItem.getText().equals("Meal")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("meal"));                }else if(menuItem.getText().equals("Accounts")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("accounts"));                }else if(menuItem.getText().equals("MemberList")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("memberList"));                }else if(menuItem.getText().equals("Back to Admin View")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("admin"));                }else if(menuItem.getText().equals("Member-View")) {                    httpSession.setAttribute("user", httpSession.getAttribute("user"));                    menuItem.getUI().ifPresent(ui -> ui.navigate("member"));                }else{                    httpSession.removeAttribute("user");                    menuItem.getUI().ifPresent(ui -> ui.navigate("login"));                }            });        });        add(image,menuBar);    }}