package bd.edu.seu.messmanagementsystemfrontend.ui.manager;import bd.edu.seu.messmanagementsystemfrontend.ui.Footer;import com.vaadin.flow.component.html.Div;import com.vaadin.flow.component.html.Image;import com.vaadin.flow.component.notification.Notification;import com.vaadin.flow.component.orderedlayout.VerticalLayout;import com.vaadin.flow.router.Route;import javax.servlet.http.HttpSession;@Route("manager")public class ManagerView extends VerticalLayout {    public ManagerView(HttpSession httpSession) {        super();        ManagerHeader managerHeader = new ManagerHeader(httpSession);        managerHeader.addAttachListener(attachEvent -> {                String userRole =(String) httpSession.getAttribute("user");                if(userRole==null) userRole="unknown";                Notification.show(userRole);                if(!userRole.equals("Manager") && !userRole.equals("Admin")){                    httpSession.removeAttribute("user");                    managerHeader.getUI().ifPresent(ui -> ui.navigate("login"));                }            });        Div body = new Div();        Footer footer = new Footer();        body.setSizeFull();        Image image = new Image();        image.setSrc("https://st2.depositphotos.com/3591429/6308/i/950/depositphotos_63082953-stock-photo-hands-holding-word-manager.jpg");        image.setSizeFull();        body.add(image);        add(managerHeader,body,footer);    }}