package bd.edu.seu.messmanagementsystemfrontend.ui.member;import bd.edu.seu.messmanagementsystemfrontend.service.MemberService;import bd.edu.seu.messmanagementsystemfrontend.ui.Footer;import com.vaadin.flow.component.html.Div;import com.vaadin.flow.component.html.Image;import com.vaadin.flow.component.notification.Notification;import com.vaadin.flow.component.orderedlayout.VerticalLayout;import com.vaadin.flow.router.Route;import org.springframework.beans.factory.annotation.Autowired;import javax.servlet.http.HttpSession;@Route("member")public class MemberView extends VerticalLayout {    @Autowired    private MemberService memberService;    public MemberView(HttpSession httpSession) {        super();        MemberHeader memberHeader = new MemberHeader(httpSession);        memberHeader.addAttachListener(attachEvent -> {            String userRole =(String) httpSession.getAttribute("user");            if(userRole==null) userRole="unknown";            Notification.show(userRole);            if(!userRole.equals("Member") && !userRole.equals("Manager") && !userRole.equals("Admin")){                httpSession.removeAttribute("user");                memberHeader.getUI().ifPresent(ui -> ui.navigate("login"));            }        });        Div body = new Div();        body.setSizeFull();        Image image = new Image();        image.setSrc("https://logos.flamingtext.com/Word-Logos/member-design-fluffy-name.png");        image.setSizeFull();        body.add(image);        Footer footer = new Footer();        add(memberHeader,body,footer);    }}